package com.gngsn.jpademo.service.impl;

import com.gngsn.jpademo.dto.PagingDTO;
import com.gngsn.jpademo.entity.Genre;
import com.gngsn.jpademo.entity.Movie;
import com.gngsn.jpademo.entity.MovieGenre;
import com.gngsn.jpademo.repository.MovieGenreRepository;
import com.gngsn.jpademo.repository.MovieId;
import com.gngsn.jpademo.repository.MovieRepository;
import com.gngsn.jpademo.repository.impl.GenreRepositoryImpl;
import com.gngsn.jpademo.service.MovieService;
import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.springframework.data.domain.Page;
import org.springframework.data.domain.PageRequest;
import org.springframework.stereotype.Service;

import java.util.List;
import java.util.Map;
import java.util.stream.Collectors;

import static java.util.stream.Collectors.groupingBy;

@Slf4j
@Service
@RequiredArgsConstructor
public class MovieServiceImpl implements MovieService {

    private final MovieRepository movieRepository;

    private final MovieGenreRepository movieGenreRepository;
    private final GenreRepositoryImpl genreRepository;

    @Override
    public Page<Movie> getMovieList(PagingDTO pagingDTO) {
        Page<Movie> movieList = movieRepository.findAll(PageRequest.of(pagingDTO.getPage(), 20));

        List<MovieId> movieIdList = movieRepository.findMovieIdTop20();
        List<MovieGenre> movieGenreList = movieGenreRepository.findByMovie_MovieIdIn(movieIdList.stream().map(MovieId::getMovieId).collect(Collectors.toList()));
        Map<Movie, List<MovieGenre>> genre = movieGenreList.stream().collect(groupingBy(MovieGenre::getMovie));

        log.info("MovieGenre List: {}", movieList);
        log.info("movieGenreList List: {}", movieGenreList);
        log.info("genre List: {}", genre);
        return movieRepository.findAll(PageRequest.of(pagingDTO.getPage(), 20));
    }

    @Override
    public List<MovieGenre> getMovieGenresByMovieId(List<Long> movieId) {
        return movieGenreRepository.findByMovie_MovieIdIn(movieId);
    }

    @Override
    public List<Genre> getMovieGenreList() {
        return genreRepository.findTopOrderByGenreIdAscLimitedTo(15);
    }
}


